package info.androidhive.listviewfeed;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;import android.app.Activity;import android.content.Intent;import android.content.SharedPreferences;import android.graphics.Color;import android.os.Bundle;import android.text.format.DateFormat;import android.util.Log;import android.view.View;import android.view.Window;import android.view.WindowManager;import android.widget.*;public class ShowMsgActivity extends Activity {	public static boolean bShowMsgActivity = false;	public static Activity mShowMsgActivity = null;	private SharedPreferences pref;	static final String TAG = "DecideApp";	@Override	public void onCreate(Bundle savedInstanceState)	{	    super.onCreate(savedInstanceState);	    mShowMsgActivity = this;	    setContentView(R.layout.activity_showmsg);	    Intent intent = getIntent();	    String strParamKill = intent.getStringExtra("kill");	    Log.i(TAG, "strParamValue" + strParamKill);	    if( strParamKill != null ) {		    if( strParamKill.equals("1") ) {		    	bShowMsgActivity = false;		    	finish();		    	return;		    }	    }	    String strParamMsgKey = intent.getStringExtra("msgKey");	    String strParamTitle = intent.getStringExtra("pushTitle");	    String strParamText = intent.getStringExtra("pushText");        AutoResizeTextView textviewMsg1 = (AutoResizeTextView) findViewById(R.id.textViewMsg1);        textviewMsg1.setText( strParamMsgKey + " " + strParamTitle + " " + strParamText );        textviewMsg1.setMaxLines(1);        Calendar c = Calendar.getInstance();        System.out.println("Current time => "+c.getTime());        SimpleDateFormat df = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        String formattedDate = df.format(c.getTime());        AutoResizeTextView textviewMsg2 = (AutoResizeTextView) findViewById(R.id.textViewMsg2);        textviewMsg2.setText( formattedDate );        textviewMsg2.setMaxLines(1);	    Button btnHide = (Button) findViewById(R.id.btnHide);        btnHide.setOnClickListener(new Button.OnClickListener() {			public void onClick(View v) {				bShowMsgActivity = false;				finish();			}		});        bShowMsgActivity = true;	}}